import { Component, OnInit, HostBinding, Input } from '@angular/core'; //import Input
import { Article } from './article.model'; //import article

@Component({
  selector: 'app-article',
  templateUrl: './article.component.html',
  styleUrls: ['./article.component.css']
})
export class ArticleComponent implements OnInit {
  @HostBinding('attr.class') cssClass = 'card';
  @Input()
  article!: Article;

  constructor() {
    //L'article è popolato dall'@Input
  }

  voteUp(): Boolean {
    this.article.voteUp();
    return false;
  }

  voteDown():Boolean {
    this.article.voteDown();
    return false; 
  }

  ngOnInit() {}
}
<div class="card-header">
  <h3> {{ article.votes }} Points </h3>
</div>
<div class="card-body">
<a href="{{ article.link }}"> {{ article.title }} </a>
 <!-- right here -->
<span class="badge badge-pill badge-info">{{article.domain()}}</span>
</div>
<div class="card-footer">
 <div class="btn-group">
    <a href (click)="voteUp()" class="btn btn-secondary"> upvote </a>
    <a href (click)="voteDown()" class="btn btn-secondary"> downvote </a>
</div>
</div>
